import React, { useState, useEffect } from 'react'

// mi componente principal <Lista>
const Lista = () => {
  // guardo el estado list de valor inicial la lista que tengo
  const [list, setList] = useState([
    {
      id: 1,
      name:'A las nueve',
      artista: 'NTVG',
      duracion: '3:24',
      votos: 0,
    },
    {
      id: 2,
      name:'Maldicion',
      artista: 'Once tiros',
      duracion: '3:04',
      votos: 0,
    },
    {
      id: 3,
      name: 'Cancion para uno',
      artista: 'La Vela Puerca',
      duracion: '2:53',
      votos: 0,
    },
    {
      id: 4,
      name: 'Mi Revolucion',
      artista: 'Cuartro Pesos de Propina',
      duracion: '3:24',
      votos: 0,
    },
    {
      id: 5,
      name: 'Lo mas simple de las cosas',
      artista: 'Hereford',
      duracion: '3:17',
      votos: 0,
    }

  ]);

  const updateVote = (editVote) => {
    const changeVote = list.map(listas => (
      listas.id === editVote.id 
      ? editVote 
      : listas
    ))
    setList(changeVote)

  }




  // utilizo useEffect para ejecutar este código sólo una vez
  useEffect(() => {
    // copio la lista con [...list] y la ordeno con sort()
    const sortedList = [...list].sort((b, a) => (a.votos > b.votos ? 1 : a.votos < b.votos ? -1 : 0))
    // actualizo el estado con la nueva lista ya ordenada
    setList(sortedList)
  }, [])

  // vuelco el contenido del estado `list`
  return (
    <>
      {/* Aquí pongo el botón para reordenar la lista */}
      <button
        onClick={() => {
          let newSortedList = [...list].sort((a, b) => (a.votos > b.votos ? 1 : a.votos < b.votos ? -1 : 0))
          // si la lista después de ordenarla tiene el mismo primer elemento, lo repito a la inversa
          // (claro que esto es ineficiente, lo suyo sería habilitar otro estado para guardar el tipo de ordenamiento que hemos hecho)
          if (newSortedList[0] === list[0])
            newSortedList = [...list].sort((b, a) => (a.votos > b.votos ? 1 : a.votos < b.votos ? -1 : 0))
          setList(newSortedList)
        }}
      >
        Ordenar
      </button>

      {/* Y aquí la lista, cada vez que el estado cambie este componente se va a repintar y a actualizar la vista */}
      <ul >
        {list.map((listas) => ( 
          <li key={listas.id}>
            {listas.id} <br />
            {listas.name}   <br /> 
            {listas.artista}  <br />  
             {listas.votos} <br /> 
             <button
              onClick={() => updateVote ({id: listas.id, name: listas.name,
               artista: listas.artista, votos: listas.votos +1 })}>
                Like
            </button>
            <button
              onClick={() => updateVote ({id: listas.id, name: listas.name,
               artista: listas.artista, votos: listas.votos -1 })}>
                Deslike
            </button>
             <hr/> 


          </li>
        ))}
      </ul>
    </>
  )
}

export default Lista
