import React from "react";
import { useHistory } from "react-router-dom";
import auth from "../Home&Login/auth";
import "./ListPlay.css";
import { useState } from 'react';



function validar(e, history){
  if (document.getElementById("input-list").value.length > 0){ 
     auth.login(() => {
       history.push("/playlist")
     })
  }

  else {
    e.preventDefault()
    alert("Rellenar los campos")

  }
}

function PlayListCreate() {
  const history = useHistory()
  const [count, setCount] = useState(5);
  const [canciones, setCanciones] = useState([
    {
      name:'A las nueve',
      artista: 'NTVG',
      duracion: '3:24',
      votos: 3,
    },
    {
      name:'A las nueve',
      artista: 'NTVG',
      duracion: '3:24',
      votos: 6,
    },
  ]);

  return (

    <div className="content-list">
      <input
        type="text"
        id="input-list"
        placeholder="  Buscar "
        className="input-table-c"
      ></input>

      <div className="table-list">
        <table className="table">
          <p className="text-table">Resultados</p>
          <tr className="elem-table">
            <th>Nombre</th>
            <th>Artista</th>
            <th>Álbum</th>
            <th>Duración</th>
            <th>Agregar</th>
          </tr>
          <tr className="linea">
            <td>A las nueve</td>
            <td>NTVG</td>
            <td>El calor del pleno invierno</td>
            <td>3:24</td>
            <button className="icon">  </button>
          </tr>
        </table>

      </div>

      <div className="table-list-two">
        <table className="table-two">
          <p className="text-table-two">Tu Playlist</p>
          <tr>
            <th>Nombre</th>
            <th>Artista</th>
            <th>Duración</th>
            <th>Cant.Votos</th>
            <th>Votar</th>
          </tr>
          <tr>
          <td>A las nueve</td>
            <td>NTVG</td>
            <td>3:24</td>
            <td> {count} </td>
            <button className="like" onClick={() => setCount(count + 1)} >  </button> <button className="icon-deslike"  onClick={() => setCount(count - 1)} ></button>
          </tr>
          <tr>
          <td>A las nueve</td>
            <td>NTVG</td>
            <td>3:24</td>
            <td> {count} </td>
            <button className="like" onClick={() => setCount(count + 1)} >  </button> <button className="icon-deslike"  onClick={() => setCount(count - 1)} ></button>
          </tr>
        </table>


      </div>
    </div>
  );
}

export default PlayListCreate;
